	(1494, 87, '_reward_for_joy_items_3_textbutton', 'field_61ec7b78cd33c'),
	(1495, 87, 'reward_for_joy_items_3_text', '<h5>Private sales</h5>\r\n<h6>5% (16,000,000) of the total number of RFJ tokens are allocated for sale to\r\nstrategic investors.</h6>\r\n<ul>\r\n 	<li>Funds raised will go toward developing the platform, marketing, and liquidity of the WBNB / RFJ pair</li>\r\n 	<li>All tokens from the private sale will be gradually unlocked and available to\r\ninvestors according to the following schedule:</li>\r\n</ul>\r\n<ol>\r\n 	<li>10% will be unlocked on the day of the public sale (Q3 2023)</li>\r\n 	<li>The rest of the private sale tokens will be unlocked quarterly over the next 2 years</li>\r\n</ol>'),
